package ru.miit;import org.jboss.weld.environment.se.Weld;import org.jboss.weld.environment.se.WeldContainer;import ru.miit.clients.GraphClient;public class App {    public static void main(String[] args) {        Weld weld = new Weld();        WeldContainer container = weld.initialize();        final int V = 5;        int source = 0;        GraphClient graphClient = container.select(GraphClient.class).get();        int[][] sourceMatrix = {                {0, 1, 9 },                {0, 2, 6 },                {0, 3, 5 },                {0, 4, 3 },                {2, 1, 2 },                {2, 3, 4 },        };        graphClient.init(V);        for (int[] pair : sourceMatrix) {            graphClient.addEdge(pair[0], pair[1], pair[2]);        }        graphClient.find(source);        container.shutdown();    }}